import React, { useState } from 'react';
import './Card.css';

const Card = ({ type, title }) => {
  const [showOverlay, setShowOverlay] = useState(false);

  const getImageSrc = (type) => {
    switch (type) {
      case 'bank-draft':
        return '/media/image1.png';
      case 'bill-of-lading':
        return '/media/image2.png';
      case 'invoice':
        return '/media/image3.png';
      case 'bank-draft-2':
        return '/media/image1.png';
      case 'bill-of-lading-2':
        return '/media/image2.png';
      default:
        return '/media/image3.png';
    }
  };

  return (
    <div className="card">
      <img src={getImageSrc(type)} alt={title} onClick={() => setShowOverlay(true)} />
      <h3>{title}</h3>
      {showOverlay && (
        <div className="overlay" onClick={() => setShowOverlay(false)}>
          <img src={getImageSrc(type)} alt={title} className="overlay-image" />
        </div>
      )}
    </div>
  );
};

export default Card;
