import React from 'react';
import { useDrag, useDrop } from 'react-dnd';
import './Card.css';

const Card = ({ id, title, type, index, moveCard, onClick }) => {
  const [, ref] = useDrag({
    type: 'CARD',
    item: { id, index },
  });

  const [, drop] = useDrop({
    accept: 'CARD',
    hover: (draggedItem) => {
      if (draggedItem.index !== index) {
        moveCard(draggedItem.index, index);
        draggedItem.index = index;
      }
    },
  });

  return (
    <div ref={(node) => ref(drop(node))} className="card" onClick={onClick}>
      <img src={`media/${type}.png`} alt={title} />
      <div>{title}</div>
    </div>
  );
};

export default Card;
