import React, { useState } from 'react';
import './Card.css';

const Card = ({ type, title }) => {
  const [showOverlay, setShowOverlay] = useState(false);

  const getImageSrc = (type) => {
    switch (type) {
      case 'bank-draft':
        return '/media/bank-draft.png';
      case 'bill-of-lading':
        return '/media/${type}.png';
      case 'invoice':
        return '/media/${type}.png';
      case 'bank-draft-2':
        return '/media/${type}.png';
      case 'bill-of-lading-2':
        return '/media/${type}.png';
      default:
        return '/media/${type}.png';
    }
  };

  return (
    <div className="card">
      <img src={getImageSrc(type)} alt={title} onClick={() => setShowOverlay(true)} />
      <h3>{title}</h3>
      {showOverlay && (
        <div className="overlay" onClick={() => setShowOverlay(false)}>
          <img src={`/media/${type}.png`} alt={title}  className="overlay-image" />
        </div>
      )}
    </div>
  );
};

export default Card;
